function switchModeOnSwitch(){var s,e=document.getElementsByTagName("html")[0],t=document.getElementsByTagName("img");if(e.classList.contains("lightui"))for(e.classList.remove("lightui"),e.classList.add("darkui"),s=0;s<t.length;++s){var i=t[s].src.replace("-light","-dark");t[s].src=i}else for(e.classList.remove("darkui"),e.classList.add("lightui"),s=0;s<t.length;++s){i=t[s].src.replace("-dark","-light");t[s].src=i}}